import { useState, useRef } from "react";
import Modal from "./Task";
import './App.css';

function ListContainer({ listId, title, priorities, setPriorities, tags, setTags, lists, tasks, setTasks, deleteTasksByListId, deleteTaskById }) {
  const modalRef = useRef();
  const overlayId = `modalOverlay-${title}`;

  const [showModal, setShowModal] = useState(false);
  const [task, setTask] = useState({ title: '', priority: [], tags: [], deadline: null });

  const [showListActions, setShowListActions] = useState(false);
  const [showMoveAll, setShowMoveAll] = useState(false);
  const [selectedListId, setSelectedListId] = useState(null);
  const [selectedListTitle, setSelectedListTitle] = useState(null);
  const [availableLists, setAvailableLists] = useState([]);
  const [showConfirmation, setShowConfirmation] = useState(false);

  const handleEditTask = (task) => {
    setTask(task);
    setShowModal(true);
    document.getElementById(overlayId).style.display = "block";
  };

  const handleAddTask = () => {
    setTask({ title: '', priority: [], tags: [], deadline: null });
    setShowModal(true);
    document.getElementById(overlayId).style.display = "block";
  };

  const handleListActionsClick = () => {
    setShowListActions(!showListActions);
  };

  const handleMoveAllClick = (list) => {
    const otherLists = lists.filter((l) => l.id !== list.id);
    setSelectedListId(list.id);
    setSelectedListTitle(list.title);
    setAvailableLists(otherLists);
    setShowMoveAll(true);
    setShowListActions(false);
  };

  const handleMoveAllSelectChange = (event) => {
    const selectedListId = parseInt(event.target.value);
    setSelectedListId(selectedListId);
    const selectedList = lists.find((list) => list.id === selectedListId);
    setSelectedListTitle(selectedList.title);
  };

  const handleDeleteAllClick = async () => {
    setShowConfirmation(true);
    document.body.classList.add('no-click');
  };

  document.addEventListener("click", (event) => {
    const confirmationContent = document.querySelector(".confirmation-content");
    if (
      confirmationContent &&
      !confirmationContent.contains(event.target) &&
      !event.target.classList.contains("material-symbols-outlined")
    ) {
      confirmationContent.classList.add("shake");
      setTimeout(() => {
        confirmationContent.classList.remove("shake");
      }, 500);
    }
  });

  const handleConfirmDelete = async () => {
    try {
      await deleteTasksByListId(listId);
      setTasks((prevState) => prevState.filter((t) => t.list_id !== listId));
      setShowConfirmation(false);
    } catch (error) {
      console.error(error);
    }
  };

  function handleClose() {
    setShowConfirmation(!showConfirmation);
    document.body.classList.remove('no-click');
  }

  const handleCloseModal = () => {
    setShowModal(false);
    document.getElementById(overlayId).style.display = "none";
  };

  return (
    <div className="list">
      <div className="list-header">
        <div className="add-task">
          <div className="add-btn" onClick={handleAddTask}>
            <span className="material-symbols-outlined">add</span>
          </div>
        </div>
        <p className="list-title">{title}</p>
        <div className="dropdown">
          <div className="drop-btn">
            <span className="material-symbols-outlined" onClick={handleListActionsClick}>more_horiz</span>
          </div>
          {showListActions && (
            <div className="dropdown-content">
              <ul>
                <li>Sort List</li>
                <li onClick={handleMoveAllClick}>Move All</li>
                <li className="delete" onClick={handleDeleteAllClick}>Delete All</li>
              </ul>
            </div>
          )}
        </div>
      </div>
      <hr />
      {tasks.filter((task) => task.list_id === listId).map((task, index) => (
        <div className="task-card" key={index} style={{ backgroundColor: `${task.priority_color}60` }} onClick={() => handleEditTask(task)}>
          <div className="task">
            <div className="card-header">
              <p className="card-title">{task.task_title}</p>
              {task.tag_colors &&
                <div className="tag-colors" style={{ display: "flex" }}>
                  {task.tag_colors.split(',').map((tagColor, index) => (
                    <div className="card-tags" key={index} style={{ backgroundColor: tagColor }}></div>
                  ))}
                </div>
              }
            </div>
            {task.deadline &&
              <p className="card-date">Deadline: {new Date(task.deadline).toLocaleDateString('en-US', { timeZone: 'UTC' })}</p>
            }
          </div>
        </div>
      ))}
      <div className="dropdown">
        {showMoveAll && (
          <div className="move-list">
            <div className="dropdown-tags">
              <div className="dropdown-header">
                <div className="dropdown-title">
                  <p>Move Tasks</p>
                </div>
                <div className="dropdown-close">
                  <span className="material-symbols-outlined" onClick={() => setShowMoveAll(false)}>close</span>
                </div>
              </div>
              <hr />
              {selectedListTitle && (
                <p>{selectedListTitle}</p>
              )}
              <div>
                <div>
                  <select value={selectedListId} onChange={handleMoveAllSelectChange}>
                    {availableLists.map((list) => (
                      <option key={list.id} value={list.id} selected={list.id === selectedListId}>{list.title}</option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      <div className="delete-confirmation">
        <div className={`confirmation-content ${showConfirmation ? "active" : ""}`}>
          <div className="close-confirmation">
            <span className="material-symbols-outlined" onClick={handleClose}>close</span>
          </div>
          <p>Are you sure you want to delete these tasks? It's irreversible.</p>
          <div className="confirmation-buttons">
            <div>
              <button type="button" onClick={handleConfirmDelete}>Confirm</button>
            </div>
            <div className="cancel-btn">
              <button type="button" onClick={handleClose}>Cancel</button>
            </div>
          </div>
        </div>
      </div>

      {showModal && (
        <>
          <Modal onClose={handleCloseModal} modalRef={modalRef} task={task} priorities={priorities} setPriorities={setPriorities} tags={tags} setTags={setTags} lists={lists} setTasks={setTasks} deleteTaskById={deleteTaskById} />
        </>
      )}
      <div id={overlayId} className="modal-overlay" onClick={() => handleCloseModal()}></div>
    </div>
  );
}

export default ListContainer;
