import ListContainer from "./ListContainer";
import './App.css';

function Lists({ priorities, tags, tasks }) {
  const currentDate = new Date();
  const month = currentDate.toLocaleString('default', { month: 'long' });
  const year = currentDate.getFullYear();

  const listTitles = [
    { id: 1, title: "Today" },
    { id: 2, title: month },
    { id: 3, title: year }
  ];

  const handleDeleteAllTasks = (listId) => {
    fetch(`/tasks/${listId}`, { method: 'DELETE' })
      .then(response => response.json())
      .then(data => console.log(data))
      .catch(error => console.error(error));
  };

  return (
    <div className="scroll-container">
      <div className="list-container">
        {listTitles.map(({ id, title }) => {

          return (
            <ListContainer key={id} title={title} priorities={priorities} tags={tags} lists={listTitles} listId={id} tasks={tasks} />
          );
        })}
      </div>
    </div>
  );
}

export default Lists;
