import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Lists from './Lists';

function Data() {
  const [priorities, setPriorities] = useState([]);
  const [tags, setTags] = useState([]);
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchPriorities = async () => {
      const res = await axios.get('http://localhost:3000/priorities');
      setPriorities(res.data);
    };
  
    const fetchTags = async () => {
      const res = await axios.get('http://localhost:3000/tags');
      setTags(res.data);
    };
  
    const fetchTasks = async () => {
      const res = await axios.get('http://localhost:3000/tasks');
      setTasks(res.data);
    };
  
    fetchPriorities();
    fetchTags();
    fetchTasks();
  }, []);
  
  const handleUpdate = async (task) => {
    try {
      await axios.put(`http://localhost:3000/tasks/${task.task_id}`, task);
      setTasks((prevState) =>
        prevState.map((t) => (t.task_id === task.task_id ? task : t))
      );
    } catch (error) {
      console.error(error);
    }
  };

  const handleDelete = async (taskId) => {
    try {
      await axios.delete(`http://localhost:3000/tasks/${taskId}`);
      setTasks((prevState) =>
        prevState.filter((t) => t.task_id !== taskId)
      );
    } catch (error) {
      console.error(error);
    }
  };
  


  return (
    <>
      <Lists priorities={priorities} tags={tags} tasks={tasks} setTasks={setTasks} />
    </>
  );
}

export default Data;
