import React, { useState } from 'react';

function Dropdown({ task, priorities, setPriorities, tags, setTags }) {
    const [showTagDropdown, setShowTagDropdown] = useState(false);

    const handleAddTagClick = () => {
        setShowTagDropdown(!showTagDropdown);
    };

    return (
        <div className="dropdown">
            {showTagDropdown && (
                <div className="dropdown-tags">
                    <div className="dropdown-header">
                        <div className="dropdown-title">
                            <p>Tags</p>
                        </div>
                        <div className="dropdown-close">
                            <span className="material-symbols-outlined">close</span>
                        </div>
                    </div>
                    <hr></hr>
                    <ul>
                        {tags.map((tag) => (
                            <li key={tag.tag_id}>
                                <label>
                                    <input type="checkbox" value={tag.tag_id} checked={task.task_tags && task.task_tags.find((taskTag) => taskTag.tag_id === tag.tag_id) ? true : false} />
                                    <span className="tag" style={{ background: tag.tag_color }}>{tag.tag_name}</span>
                                </label>
                                <span className="material-symbols-outlined edit">edit</span>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
            <div className="add-tags">
                <div className="tagDrop-btn">
                    <span className="material-symbols-outlined" onClick={handleAddTagClick}>add</span>
                </div>
            </div>
        </div>
    );
}

export default Dropdown;
