import React, { useState } from 'react';

function Dropdown({ task, priorities, setPriorities, tags, setTags }) {
    const [showPriorityDropdown, setShowPriorityDropdown] = useState(false);
    const [selectedPriority, setSelectedPriority] = useState(task.priorities || []);


    const handleAddPriorityClick = () => {
        setShowPriorityDropdown(!showPriorityDropdown);
    };

    const handlePrioritySelection = (priority) => {
        if (selectedPriority != null && selectedPriority.name === priority.name) {
            setSelectedPriority(null);
        } else {
            setSelectedPriority(priority);
        }
    };

    const handlePriorityChange = (event) => {
        const selectedPriority = event.target.value;
        setPriorities([selectedPriority]);
    };

    const defaultPriority = task.priority_id;

    return (
        <div className="dropdown">
            {showPriorityDropdown && (
                <div className="dropdown-tags">
                    <div className="dropdown-header">
                        <div className="dropdown-title">
                            <p>Priorities</p>
                        </div>
                        <div className="dropdown-close">
                            <span className="material-symbols-outlined"  onClick={() => setShowPriorityDropdown(false)}>close</span>
                        </div>
                    </div>
                    <hr />
                    <ul>
                        {priorities.map((priority) => (
                            <li key={priority.priority_id}>
                                <label>
                                    <input type="checkbox" value={priority.priority_id} checked={defaultPriority === priority.priority_id} onChange={handlePriorityChange} />
                                    <span className="priority" style={{ background: priority.priority_color }}>{priority.priority_name}</span>
                                </label>
                                <span className="material-symbols-outlined edit">edit</span>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
            <div className="add-tags">
            {selectedPriority != null && (
            <div className="selected-tags">
                <div className="priorities">
                    <span className="priority" key="priority" style={{ background: selectedPriority.priority_color }}>{selectedPriority.priority_name}</span>
                </div>
            </div>
        )}
                <div className="tagDrop-btn">
                    <span className="material-symbols-outlined" onClick={handleAddPriorityClick}>add</span>
                </div>
            </div>
        </div>
    );
}

export default Dropdown;
