import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Data from './data';
import Settings from './Settings';
import Login from './Login';
import './navigation.css';
import './darkmode.css'; // import the darkmode.css file

function App() {
  const [darkMode, setDarkMode] = useState(false); // set up state for darkmode

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  }

  return (
    <>
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Data />} />
          <Route path="/lists" element={<Data />} />
          <Route path="/login" element={<Login />} />
          <Route path="/settings" element={<Settings toggleDarkMode={toggleDarkMode} />} />
        </Routes>
      </BrowserRouter>

      {/* Conditionally apply the styles based on the darkMode state */}
      <style>
        {darkMode ? `
          body {
            background-color: #333333;
            color: #ffffff;
          }
          /* add other styles for dark mode here */
        ` : ''}
      </style>
    </>
  );
}

export default App;
