import React, { useState, useEffect } from 'react';

function TagDropdown({ task, tags, setTags }) {
    const [showTagDropdown, setShowTagDropdown] = useState(false);
    const [selectedTags, setSelectedTags] = useState([]);
  
    // Initialize selected tags with the tags of the task
    useEffect(() => {
      const taskTags = tags.filter((tag) =>
        task.tags.some((taskTag) => taskTag.tag_id === tag.tag_id)
      );
      setSelectedTags(taskTags);
    }, [task, tags]);
  
    const handleAddTagClick = () => {
      setShowTagDropdown(!showTagDropdown);
    };
  
    const handleTagSelection = (tag) => {
      const tagIndex = selectedTags.findIndex(
        (selectedTag) => selectedTag.tag_id === tag.tag_id
      );
      if (tagIndex === -1) {
        setSelectedTags([...selectedTags, tag]);
      } else {
        setSelectedTags([
          ...selectedTags.slice(0, tagIndex),
          ...selectedTags.slice(tagIndex + 1),
        ]);
      }
    };
  
    const handleDocumentClick = (e) => {
      if (!e.target.closest(".dropdown") && !e.target.closest(".dropdown-tags")) {
        setShowTagDropdown(false);
      }
    };
  
    return (
      <div className="dropdown">
        {showTagDropdown && (
          <div className="dropdown-tags">
            <div className="dropdown-header">
              <div className="dropdown-title">
                <p>Tags</p>
              </div>
              <div className="dropdown-close">
                <span
                  className="material-symbols-outlined"
                  onClick={() => setShowTagDropdown(false)}
                >
                  close
                </span>
              </div>
            </div>
            <hr />
            <ul>
              {tags.map((tag, index) => (
                <li key={index}>
                  <label>
                    <input
                      type="checkbox"
                      checked={selectedTags.some(
                        (selectedTag) => selectedTag.tag_id === tag.tag_id
                      )}
                      onChange={() => handleTagSelection(tag)}
                    />
                    <span
                      className="tag"
                      style={{ background: tag.tag_color }}
                    >
                      {tag.tag_name}
                    </span>
                  </label>
                  <span className="material-symbols-outlined edit">edit</span>
                </li>
              ))}
            </ul>
          </div>
        )}
        <div className="add-tags">
          {selectedTags.length > 0 && (
            <div className="selected-tags">
              <div className="tags">
                {selectedTags.map((tag, index) => (
                  <span
                    className="tag"
                    key={index}
                    style={{ background: tag.tag_color }}
                  >
                    {tag.tag_name}
                  </span>
                ))}
              </div>
            </div>
          )}
          <div className="tagDrop-btn">
            <span
              className="material-symbols-outlined"
              onClick={handleAddTagClick}
            >
              add
            </span>
          </div>
        </div>
      </div>
    );
  }
  
export default TagDropdown;
