import React, { useState } from 'react';

function Dropdown({ task, priorities, setPriorities, tags, setTags }) {
    const [selectedPriority, setSelectedPriority] = useState(
      priorities.find(p => p.name === task.priority) || null
    );
    const [showPriorityDropdown, setShowPriorityDropdown] = useState(false);
  
    const handlePrioritySelection = (priority) => {
      if (
        selectedPriority != null &&
        selectedPriority.priority_id === priority.priority_id
      ) {
        setSelectedPriority(null);
      } else {
        setSelectedPriority(priority);
      }
    };
  
    const handleAddPriorityClick = () => {
      setShowPriorityDropdown(!showPriorityDropdown);
    };
  
    const handlePriorityChange = (event) => {
      const selectedPriority = priorities.find(
        (priority) => priority.priority_id === parseInt(event.target.value)
      );
      setSelectedPriority(selectedPriority);
      setPriorities([selectedPriority]);
    };
  
    return (
      <div className="dropdown">
        {showPriorityDropdown && (
          <div className="dropdown-tags">
            <div className="dropdown-header">
              <div className="dropdown-title">
                <p>Priorities</p>
              </div>
              <div className="dropdown-close">
                <span
                  className="material-symbols-outlined"
                  onClick={handleAddPriorityClick}
                >
                  close
                </span>
              </div>
            </div>
            <hr></hr>
            <ul>
              {priorities.map((priority, index) => (
                <li key={index}>
                  <label>
                    <input
                      type="checkbox"
                      value={priority.priority_id}
                      checked={
                        selectedPriority != null &&
                        selectedPriority.priority_id === priority.priority_id
                      }
                      onChange={() => handlePrioritySelection(priority)}
                    />
                    <span
                      className="priority"
                      style={{ background: priority.priority_color }}
                    >
                      {priority.priority_name}
                    </span>
                  </label>
                  <span
                    className="material-symbols-outlined edit"
                    onClick={() => handleEditPriorityDropdown(priority)}
                  >
                    edit
                  </span>
                </li>
              ))}
            </ul>
          </div>
        )}
        <div className="add-tags">
          {selectedPriority != null && (
            <div className="selected-tags">
              <div className="priorities">
                <div
                  className="priority"
                  key="priority"
                  style={{ background: selectedPriority.priority_color }}
                >
                  {selectedPriority.priority_name}
                </div>
              </div>
            </div>
          )}
          <div className="tagDrop-btn">
            <span
              className="material-symbols-outlined"
              onClick={handleAddPriorityClick}
            >
              add
            </span>
          </div>
        </div>
      </div>
    );
  }
  

export default Dropdown;
