import ListContainer from "./ListContainer";
import './App.css';

function Lists({ priorities, tags, tasks }) {
  const currentDate = new Date();
  const month = currentDate.toLocaleString('default', { month: 'long' });
  const year = currentDate.getFullYear();

  const listTitles = [
    { id: 1, title: "Today" },
    { id: 2, title: month },
    { id: 3, title: year }
  ];

  return (
    <div className="scroll-container">
      <div className="list-container">
        {listTitles.map(({ id, title }) => {
          // Filter tasks based on their list_id
          const listTasks = tasks.filter(task => task.list_id === id);
          return (
            <ListContainer 
              key={id} 
              title={title} 
              priorities={priorities} 
              tags={tags} 
              lists={listTitles} 
              tasks={listTasks} // Pass filtered tasks to ListContainer
            />
          );
        })}
      </div>
    </div>
  );
}


export default Lists;
