import React, { useState, useEffect } from 'react';

function TagDropdown({ task, tags, setTags }) {
    const [showTagDropdown, setShowTagDropdown] = useState(false);
    const [selectedTags, setSelectedTags] = useState(task.tag_ids ? task.tag_ids.split(",") : []);

    const handleAddTagClick = () => {
        setShowTagDropdown(!showTagDropdown);
    };

    const handleTagSelection = (tag) => {
        if (selectedTags.includes(tag.tag_id.toString())) {
            setSelectedTags(selectedTags.filter((selectedTag) => selectedTag !== tag.tag_id.toString()));
        } else {
            setSelectedTags([...selectedTags, tag.tag_id.toString()]);
        }
    };

    useEffect(() => {
        if (task.tag_ids && task.tag_colors) {
            const tagIds = task.tag_ids.split(",");
            const tagColors = task.tag_colors.split(",");
            const selectedTags = tags.filter((tag) => tagIds.includes(tag.tag_id.toString())).map((tag, index) => ({ ...tag, tag_color: tagColors[index] }));
            setSelectedTags(selectedTags);
        }
    }, [task, tags]);

    useEffect(() => {
        document.addEventListener("click", handleDocumentClick);

        return () => {
            document.removeEventListener("click", handleDocumentClick);
        };
    }, []);


    const handleDocumentClick = (e) => {
        if (!e.target.closest(".dropdown") && !e.target.closest(".dropdown-tags")) {
            setShowTagDropdown(false);
        }
    };

    return (
        <div className="dropdown">
            {showTagDropdown && (
                <div className="dropdown-tags">
                    <div className="dropdown-header">
                        <div className="dropdown-title">
                            <p>Tags</p>
                        </div>
                        <div className="dropdown-close">
                            <span className="material-symbols-outlined" onClick={() => setShowTagDropdown(false)}>close</span>
                        </div>
                    </div>
                    <hr />
                    <ul>
                        {tags.map((tag, index) => (
                            <li key={index}>
                                <label>
                                    <input type="checkbox" checked={selectedTags.includes(tag)} onChange={() => handleTagSelection(tag)} />
                                    <span className="tag" style={{ background: tag.tag_color }}>{tag.tag_name}</span>
                                </label>
                                <span className="material-symbols-outlined edit">edit</span>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
            <div className="add-tags">
                {selectedTags.length > 0 && (
                    <div className="selected-tags">
                        <div className="tags">
                            {selectedTags.map((tag, index) => (
                                <span className="tag" key={index} style={{ background: tag.tag_color }}>{tag.tag_name}</span>
                            ))}
                        </div>
                    </div>
                )}
                <div className="tagDrop-btn">
                    <span className="material-symbols-outlined" onClick={handleAddTagClick}>add</span>
                </div>
            </div>
        </div>
    );
}

export default TagDropdown;
