import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Data from './data';
import Settings from './Settings';
import Login from './Login';
import Features from './Features';
import { GoogleOAuthProvider } from '@react-oauth/google';

function App() {
  const [darkMode, setDarkMode] = useState(localStorage.getItem('darkMode') === 'true');

  useEffect(() => {
    localStorage.setItem('darkMode', darkMode);
    const className = darkMode ? 'dark-mode' : 'light-mode';
    document.querySelector('html').classList.add(className);
    return () => {
      document.querySelector('html').classList.remove(className);
    };
  }, [darkMode]);

  return (
    <GoogleOAuthProvider clientId="189940497823-ktv9nu0hu5j5dqcfvstmd2tlt9uva9h7.apps.googleusercontent.com" redirectUri="http://localhost">
      <>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<Data />} />
            <Route path="/lists" element={<Data />} />
            <Route path="/login" element={<Login />} />
            <Route path="/settings" element={<Settings darkMode={darkMode} setDarkMode={setDarkMode} />} />
            <Route path="/features" element={<Features />} />
          </Routes>
        </BrowserRouter>
      </>
    </GoogleOAuthProvider>
  );
}

export default App;
