import React, { useState, useEffect } from 'react';

function MoveList({ tasks, selectedListId, availableLists }) {
    const [showMoveListWindow, setShowMoveListWindow] = useState(false);
    const [selectedList, setSelectedList] = useState(selectedListId);

    const handleAddPriorityClick = () => {
        showMoveListWindow(!setShowMoveListWindow);
    };

    useEffect(() => {
        document.addEventListener("click", handleDocumentClick);

        return () => {
            document.removeEventListener("click", handleDocumentClick);
        };
    }, []);

    const handleDocumentClick = (e) => {
        if (!e.target.closest(".dropdown") && !e.target.closest(".dropdown-tags")) {
            setShowMoveListWindow(false);
        }
    };

    const handleSelectChange = (e) => {
        setSelectedList(e.target.value);
    };

    return (
        <div className="dropdown">
            {showMoveListWindow && (
                <div className="dropdown-tags">
                        <div className="dropdown-title">
                            <p>Move Task</p>
                        </div>
                        <div className="dropdown-close">
                            <span className="material-symbols-outlined" onClick={() => setShowMoveListWindow(false)}>close</span>
                        </div>
                    <hr />
                    <ul>
                       
                    </ul>
                </div>
            )}
            <div className="add-tags">
                <div className="tagDrop-btn">
                    <span className="material-symbols-outlined" onClick={handleAddPriorityClick}>add</span>
                </div>
            </div>
        </div>
    );
}


export default MoveList;
